import React, { useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../CSS/Navbar.css';
import { useSelector } from 'react-redux';

const Navbar = (props) => {
  const totalQuantity = useSelector((state) => state.cart.quantity);
  const navigate = useNavigate();

  // Reset the category to null on every render
  useEffect(() => {
    props.setCategory(null);
  }, []);

  // Handle category change based on user selection
  const handleCategoryChange = (event) => {
    const selectedCategory = event.target.value;

    // Set the category if a valid option is selected
    if (selectedCategory) {
      props.setCategory(selectedCategory);
    }

    // Navigate to the menu page after category change
    navigate('/menu');
  };

  return (
    <div className='navbar'>
      <div className="left">
        <h2>Foodie's Restaurant</h2>
      </div>
      <ul className='nav-ul'>
        <li className='nav-li'>
          <Link className='navlink' to="/">Home</Link>
        </li>
        <li className='nav-li'>
          <Link className='navlink' to="/menu">Menu</Link>
        </li>
        <li className='nav-li dropdown'>
          Cuisine
          <select className='selecnav' name="cuisine" id="cuisine" onChange={handleCategoryChange}>
            <option value="">Select Cuisine</option>
            <option value="pakistani">Pakistani</option>
            <option value="chinese">Chinese</option>
          </select>
        </li>
        <li className='nav-li'>
          <Link className='navlink' to="/contact">Contact Us</Link>
        </li>
      </ul>
      <div className="right">
        <button>Order Online</button>
        <Link to='/cart'><i className="fa-solid fa-cart-shopping"></i></Link>
        <p className="circcc">{totalQuantity}</p>
        <p>Login/Signup</p>
      </div>
    </div>
  );
};

export default Navbar;
