import React from 'react';
import '../CSS/Categories.css';
import Display from '../JSX/DisplayCategory';  // Make sure the file name is correct
import categories from '../../Assets/categories/categories';

const Categories = (props) => {
    const hey = (item) => {
        props.setCategory(item.category);
    };

    return (
        <div className='category'>
            <div className='cat-cat'>
                <h1>Categories</h1>
                <div className='inner-cont'>
                    {categories.map((item, index) => {
                        return (
                            // Key should be on the div containing the mapped element
                            <div key={index} onClick={() => hey(item)}>
                                <Display image={item.image} text={item.category} />
                            </div>
                        );
                    })}
                </div>
            </div>
        </div>
    );
};

export default Categories;
