import React, { useState } from 'react';
import '../CSS/LoginSignup.css';

const SignUp = () => {
  const [upperHeading, setUpperHeading] = useState('Login Form');
  const [isTransitioning, setIsTransitioning] = useState(false);

  const handleFormSwitch = (form) => {
    setIsTransitioning(true); // Start transition
    setTimeout(() => {
      setUpperHeading(form);
      setIsTransitioning(false); // End transition after 300ms
    }, 300); // Match with CSS animation duration
  };

  return (
    <div className='login-signUp'>
      <h2>{upperHeading}</h2>
      <div className="log-sig-but">
        <button className='log-sig-but' onClick={() => handleFormSwitch('Login Form')}>Login</button>
        <button className='log-sig-but' onClick={() => handleFormSwitch('Signup Form')}>Sign Up</button>
      </div>

      <div className={`main-log-sig-container ${isTransitioning ? 'fade-slide' : ''}`}>
        {upperHeading === 'Login Form' ? (
          <form className='form-log-sig'>
            <label htmlFor="email">
              Email:
              <input className='inp-log-sig' type="email" name="email" id="email" />
            </label>
            <label htmlFor="password">
              Password:
              <input className='inp-log-sig' type="password" name="password" id="password" />
            </label>
            <div>
              <span>Not a member?</span>
              <span style={{ cursor: 'pointer' }} onClick={() => handleFormSwitch('Signup Form')}>Signup now</span>
            </div>
          </form>
        ) : (
          <form className='form-log-sig'>
            <label htmlFor="email">
              Email:
              <input className='inp-log-sig' type="email" name="email" id="email" />
            </label>
            <label htmlFor="password">
              Password:
              <input className='inp-log-sig' type="password" name="password" id="password" />
            </label>
            <label htmlFor="confirmPassword">
              Confirm Password:
              <input className='inp-log-sig' type="password" name="confirmPassword" id="confirmPassword" />
            </label>
          </form>
        )}
      </div>
    </div>
  );
};

export default SignUp;
