import React, { useState } from 'react';
import '../CSS/LoginSignup.css';

const SignUp = () => {
  const [upperHeading, setUpperHeading] = useState('Login Form');
  const setButtonForSignUp=()=>{
  setUpperHeading('Signup Form')
  }
  const setButtonForLogin=()=>{
    setUpperHeading('Login Form')
    }
  return (
    <div className='login-signUp'>
      <h2>{upperHeading}</h2>
      <div className="log-sig-but">
        <button onClick={setButtonForLogin}>Login</button>
        <button onClick={setButtonForSignUp}>Sign Up</button>
      </div>
      {upperHeading === 'Login Form' ? (
        <div>
          <form action="">
            <label htmlFor="email">
              Email:
              <input type="email" name="email" id="email" />
            </label>
            <label htmlFor="password">
              Password:
              <input type="password" name="password" id="password" />
            </label>
          </form>
        </div>
      ) :
        (
          <div>
            
          </div>
        )
      }


    </div>
  );
};

export default SignUp;
