import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';
import Navbar from './Components/JSX/Navbar';
import Hero from './Components/JSX/Hero';
import AboutRestaurant from './Components/JSX/AboutRestaurant';
import Special from './Components/JSX/Special';
import Menu from './Pages/JSX/Menu';
import Cart from './Pages/JSX/Cart';
import ItemDisplay from './Components/JSX/ItemDisplay';

function App() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [category, setCategory] = useState(null); // Track selected category
  log
  // useEffect=(()=>{

  // },setCategory)

  return (
    <Router>
      <div className="App">
        <Navbar setCategory={setCategory} /> {/* Pass setCategory to Navbar */}
        <Routes>
          <Route path="/" element={<> <Hero /><AboutRestaurant /><Special /></>} />
          <Route path="/about" element={<AboutRestaurant />} />
          <Route path="/special" element={<Special />} />
          <Route path="/menu" element={<Menu category={category} setItem={setSelectedItem} />} /> {/* Pass category */}
          <Route path="/cart" element={<Cart />} />
          <Route path="/productshow" element={<ItemDisplay item={selectedItem} setItem={setSelectedItem} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
