import React, { useState } from 'react';
import '../CSS/LoginSignup.css';

const SignUp = () => {
  const [upperHeading, setUpperHeading] = useState('LoginForm');

  const toggleForm = () => {
    setUpperHeading((prevHeading) =>
      prevHeading === 'LoginForm' ? 'SignupForm' : 'LoginForm'
    );
  };

  return (
    <div className='login-signUp'>
      <h2>{upperHeading === 'LoginForm' ? 'Login' : 'Sign Up'}</h2>

      {upperHeading === 'LoginForm' ? (
        <div>
          <form action="">
            <label htmlFor="email">
              Email:
              <input type="email" name="email" id="email" />
            </label>
            <label htmlFor="password">
              Password:
              <input type="password" name="password" id="password" />
            </label>
          </form>
        </div>
      ) : (
        <div>
          <form action="">
            <label htmlFor="name">
              Name:
              <input type="text" name="name" id="name" />
            </label>
            <label htmlFor="email">
              Email:
              <input type="email" name="email" id="email" />
            </label>
            <label htmlFor="password">
              Password:
              <input type="password" name="password" id="password" />
            </label>
          </form>
        </div>
      )}

      <button onClick={toggleForm}>
        {upperHeading === 'LoginForm' ? 'Switch to Signup' : 'Switch to Login'}
      </button>
    </div>
  );
};

export default SignUp;
