import React, { useState, useEffect } from 'react';
import image1 from '../../Assets/barbeque.jpeg';
import image2 from '../../Assets/chocolate_cake.jpg';
import image3 from '../../Assets/egg_fried_rice.jpg';
import image4 from '../../Assets/fast food.jpeg';
import image5 from '../../Assets/garlic_bread.jpg';


const pictures = [
    {
        id: 1,
        image: image1,
        text: 'Delicious Barbeque, grilled to perfection!',
    },
    {
        id: 2,
        image: image2,
        text: 'Indulge in our rich and creamy Chocolate Cake.',
    },
    {
        id: 3,
        image: image3,
        text: 'Savor the flavor of our Egg Fried Rice.',
    },
    {
        id: 4,
        image: image4,
        text: 'Quick bites of your favorite Fast Food.',
    },
    {
        id: 5,
        image: image5,
        text: 'Taste the goodness of our Garlic Bread.',
    },
];

const AboutRestaurant = () => {
    const [currentIndex, setCurrentIndex] = useState(0); // Start from 0

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentIndex((prevIndex) => (prevIndex + 1) % pictures.length);
            console.log(currentIndex);
            
        }, 1000);

        return () => clearInterval(interval);
    }, []);


    return (
        <div className='about-restaurant'>
            <div className="left">
                <img src={pictures[currentIndex].image} alt={`Dish ${currentIndex + 1}`} />
            </div>
            <div className="right">
                <h2>About Our Restaurant</h2>
                <p>{pictures[currentIndex].text}</p>
            </div>
        </div>
    );
};

export default AboutRestaurant;
