import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../CSS/Navbar.css';
import { useSelector } from 'react-redux';

const Navbar = (props) => {
  const totalQuantity = useSelector((state) => state.cart.quantity);
  const navigate = useNavigate();
  const [selectedCategory, setSelectedCategory] = useState(null); // Local state to track selected category

  // Reset category on every render except when it changes (this runs first on each render)
  useEffect(() => {
    // Reset category to null whenever the component renders
    props.setCategory(null); 
  }, []); // Empty dependency array ensures this runs only on the initial mount

  // Handle category change (this will also trigger useEffect)
  const handleCategoryChange = (event) => {
    const selectedCategory = event.target.value;
    setSelectedCategory(selectedCategory); // Update local state when category changes

    if (selectedCategory) {
      props.setCategory(selectedCategory); // Set the selected category in the parent
    } else {
      props.setCategory(null); // If no category selected, reset to null
    }

    navigate('/menu'); // Navigate to the menu page
  };

  // Make sure that useEffect runs first but the handleCategoryChange updates category correctly
  useEffect(() => {
    if (selectedCategory !== null) {
      // This ensures useEffect runs after category changes
      props.setCategory(selectedCategory); 
    }
  }, [selectedCategory]); // Run whenever the selectedCategory changes

  return (
    <div className='navbar'>
      <div className="left">
        <h2>Foodie's Restaurant</h2>
      </div>
      <ul className='nav-ul'>
        <li className='nav-li'>
          <Link className='navlink' to="/">Home</Link>
        </li>
        <li className='nav-li'>
          <Link className='navlink' to="/menu">Menu</Link>
        </li>
        <li className='nav-li dropdown'>
          Cuisine
          <select
            className='selecnav'
            name="cuisine"
            id="cuisine"
            onChange={handleCategoryChange}
            value={selectedCategory || ""}
          >
            <option value="">Select Cuisine</option>
            <option value="pakistani">Pakistani</option>
            <option value="chinese">Chinese</option>
          </select>
        </li>
        <li className='nav-li'>
          <Link className='navlink' to="/contact">Contact Us</Link>
        </li>
      </ul>
      <div className="right">
        <button>Order Online</button>
        <Link to='/cart'><i className="fa-solid fa-cart-shopping"></i></Link>
        <p className="circcc">{totalQuantity}</p>
        <p>Login/Signup</p>
      </div>
    </div>
  );
};

export default Navbar;
