    { title: 'Starters', items: starters },
    { title: 'Chinese Cuisine', items: chinese },
    { title: 'Desserts', items: desserts },
    { title: 'Drinks and Beverages', items: drinks },
    { title: 'Pakistani Cuisine', items: pakistani }
  ];
