    <div className='menu'>
      {
        allMenuArray.map((menu, index) => {
          return ( 
            <div key={index} className='menuie'>
              <h2 style={{ textAlign: 'center' }}>{menu.title}</h2>
              <div className="meu-items">
                {menu.items.map((elem, indexagain) => {
