import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../CSS/Navbar.css';
import { useSelector } from 'react-redux';

const Navbar = (props) => {
  const totalQuantity = useSelector((state) => state.cart.quantity);
  const navigate = useNavigate();
  const [selectedCategory, setSelectedCategory] = useState(null); // Local state to track selected category

  // Reset category on every render except when it changes
  useEffect(() => {
    props.setCategory(null); // Reset category to null on every render
  }, []); // Empty dependency array to ensure it only runs on initial render

  // Handle category change
  const handleCategoryChange = (event) => {
    const selectedCategory = event.target.value;
    if (selectedCategory) {
      setSelectedCategory(selectedCategory); // Update local state when category changes
      props.setCategory(selectedCategory); // Pass selected category to parent
    } else {
      setSelectedCategory(null); // Reset local state when no category is selected
      props.setCategory(null); // Reset category in parent component
    }
    navigate('/menu'); // Navigate to the menu page
  };

  return (
    <div className='navbar'>
      <div className="left">
        <h2>Foodie's Restaurant</h2>
      </div>
      <ul className='nav-ul'>
        <li className='nav-li'>
          <Link className='navlink' to="/">Home</Link>
        </li>
        <li className='nav-li'>
          <Link className='navlink' to="/menu">Menu</Link>
        </li>
        <li className='nav-li dropdown'>
          Cuisine
          <select className='selecnav' name="cuisine" id="cuisine" onChange={handleCategoryChange} value={selectedCategory || ""}>
            <option value="">Select Cuisine</option>
            <option value="pakistani">Pakistani</option>
            <option value="chinese">Chinese</option>
          </select>
        </li>
        <li className='nav-li'>
          <Link className='navlink' to="/contact">Contact Us</Link>
        </li>
      </ul>
      <div className="right">
        <button>Order Online</button>
        <Link to='/cart'><i className="fa-solid fa-cart-shopping"></i></Link>
        <p className="circcc">{totalQuantity}</p>
        <p>Login/Signup</p>
      </div>
    </div>
  );
};

export default Navbar;
