import React, { useState } from 'react';
import '../CSS/Item.css';
import { addToCart } from '../../Redux/StoreSlice/CartSlice';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';

const Item = ({ elem, setItem }) => {
  const dispatch = useDispatch();
  const [message, setSuccessMessage] = useState('');

  const add = (payload) => {
    dispatch(addToCart(payload));
    setSuccessMessage('Added to cart successfully!');
    setTimeout(() => setSuccessMessage(''), 3000);
  };

  const handleItemClick = () => {
    setItem(elem); // Set selected item in App.js state
  };

  return (
    <Link to="/productshow" onClick={handleItemClick}>
      <div className="Item">
        <img className="item-image" src={elem.picture} alt="" />
        <p>{elem.name}</p>
        <button onClick={() => add(elem)}>Add To Cart</button>
        {message && <p className="success-message">{message}</p>}
      </div>
    </Link>
  );
};

export default Item;
