import React from 'react';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import video from '../../Assets/6320525_Grill_Barbecue_3840x2160.mp4'; // Import the video
import '../CSS/Hero.css'; // Import CSS for styling

const Hero = () => {
  return (
    <div className='Hero'>
      <div className='video-container'>
        <video src={video} autoPlay loop muted autoPlay 
          loop 
          muted 
          playsInline 
          controls ></video>
        <div className='content'>
          <h1>Welcome to Foodie's Paradise</h1>
          <p>Experience a culinary journey like no other! Indulge in our delicious dishes made with fresh ingredients and love.</p>
        </div>
      </div>
    </div>
  );
};

export default Hero;
