import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

import Navbar from './Components/JSX/Navbar';
import Hero from './Components/JSX/Hero';
import AboutRestaurant from './Components/JSX/AboutRestaurant';
import Special from './Components/JSX/Special';
import Cuisine from './Components/JSX/Cuisine';
import Menu from './Pages/JSX/Menu';
import Cart from './Pages/JSX/Cart';
import ItemDisplay from './Components/JSX/ItemDisplay';

function App() {
  const [selectedItem, setSelectedItem] = useState(null); 


  return (
    <Router>
      <div className="App">
        <Navbar />
        <Routes>
          <Route path="/" element={<><Hero /><AboutRestaurant /><Special /><Cuisine /></>} />
          <Route path="/about" element={<AboutRestaurant />} />
          <Route path="/special" element={<Special />} />
          <Route path="/cuisine" element={<Cuisine />} />
          <Route path="/menu" element={<Menu setItem={ItemHandler} />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/productshow" item={selectedItem} /> {/* Pass selected item data */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
